mapper.insert:
 INSERT INTO contract
 (product_id, status, start_date, end_date, period, total_premium)
 VALUES(#{productId},
        #{status},
        #{contractStartDate},
        #{contractEndDate},
        #{period},
        #{totalPremium}
);

INSERT INTO product_collaterial
    (product_id, contract_id, collateral_id, is_sign)
VALUES(#{productId},
       #{contractId},
       #{collateralId},
       #{isSign}
    );

mapper.select:
SELECT id, name, period
FROM product p
where id = #{productId}
