mapper.insert:
 INSERT INTO contract
 (product_id, status, start_date, end_date, period, total_premium)
 VALUES(#{productId},
        #{status},
        #{contractStartDate},
        #{contractEndDate},
        #{period},
        #{totalPremium}
);

INSERT INTO product_collaterial
    (product_id, contract_id, collateral_id)
VALUES(#{productId},
       #{contractId},
       #{collateralId}
    );

mapper.select:
SELECT id, name, period
FROM product p
where id = #{productId}

mapper.update:
UPDATE product_collaterial
SET is_sign #{isSign}
WHERE product_id = #{productId} AND contract_id = #{contractId} AND collateral_id = #{collateralId}

UPDATE contract
SET total_premium = #{totalPremium}
WHERE id = #{contractId}

UPDATE contract
SET total_premium = #{totalPremium},
    period = #{period}
WHERE id = #{contractId}

UPDATE contract
SET status = #{status}
WHERE id = #{contractId}
